SELECT *
	FROM MEMBER
WHERE MID = #{mId}
AND MPWD = #{mPwd}
AND M_STATUS = 'Y'

SELECT MID
	FROM MEMBER
WHERE MID = #{mId}

SELECT MPWD
	FROM MEMBER
WHERE MID = #{mId}

SELECT MID FROM MEMBER
WHERE MID = #{mId}
AND EMAIL_CODE = #{authkey}
AND EMAIL_CHECK = 'N'

SELECT EMAIL
	FROM MEMBER
WHERE EMAIL = #{email}
